import countries from 'world-countries';

function CountryInfo(props){
    countries.sort((a, b) => b.area - a.area)
    let ratio = (props.data.area/countries[0].area * 100 + "%")

      function bigCountry(){
        return(
        <div className="countryInfo">

        <p className="countryName">{props.data.name.common} {props.data.area} KM <sup>2</sup></p>
        <div style={{width: ratio, height: '20px', backgroundColor: "Blue", visibility: "visible"}}></div>

        <small>Capital: {props.data.capital}</small>
        <small><br></br>Region: {props.data.subregion}</small>

        </div>
        )
      }
      function smallCountry(){
      return(
        <div className="countryInfo">

        <p className="countryName">{props.data.name.common} {props.data.area} KM <sup>2</sup></p>
        
        <div style={{width: '100%', backgroundColor:'light-gray'}}>
        <div style={{width: ratio, height: '20px', backgroundColor: "Blue", visibility: "visible"}}></div>
        </div>
        


        </div>
        )
        }
      

    return (

    <>

        {
            
        //.filter(props.data => props.data.common.name = "Antarctica")
            props.detailed ? bigCountry() : smallCountry()

        }

    </>

    )
}



export default CountryInfo;
