import countries from 'world-countries';

function CountryInfo(props){
    countries.sort((a, b) => b.area - a.area)
    let ratio = (props.data.area/countries[0].area * 100 + "%")

      function bigCountry(props){
        return(
        <div className="countryInfo">

        <p className="countryName">{props.country.name.common} {props.country.area} KM <sup>2</sup></p>
        <small>Capital: {props.country.capital}</small>
        <small><br></br>Region: {props.country.subregion}</small>


        <div style={{width: ratio, height: "10px", backgroundColor: "Blue", visibility: "visible"}}></div>

        </div>
        )
      }
      function smallCountry(props){
      return(
        <div className="countryInfo">

        <p className="countryName">{props.country.name.common} {props.country.area} KM <sup>2</sup></p>

        <div style={{width: ratio, height: "10px", backgroundColor: "Green", visibility: "visible"}}></div>

        </div>
        )
        }
      

    return (

    <>

        {

            props.data.map((country, index) => 
                index > 4 ? bigCountry(country) : smallCountry(country)
                            
                )
            

        //.filter(props.data => props.data.common.name = "Antarctica")

        }

    </>

    )
}



export default CountryInfo;
