import "./lab1.css";
import countries from "world-countries";
import CountryInfo from "./CountryInfo";
import React, { useState } from "react";

function Lab1() {
  countries.sort((a, b) => b.area - a.area);
  var usableCountries = countries.filter((c) => c.name.common !== "Antarctica");

  var searchCountries = useState(0);
  const [search, SetSearch] = useState("");

  function ChangeSearch(event) {
    SetSearch(event.target.value);
  }

  usableCountries = countries.filter((c) => c.name.common.includes(search));

  //var bigCountries = usableCountries.splice(0, 5);

  return (
    <div
      className="App"
      style={{ display: "flex", width: "50%", backgroundColor: "grey" }}
    >
      <input
        style={{ display: "block" }}
        type="text"
        onChange={ChangeSearch}
      ></input>
      {/*      <div
        style={{
          float: "left",
          width: "50%",
          display: "flex",
          alignItems: "stretch",
          flexDirection: "column",
          justifyContent: "space-between",
        }}
      >
        {bigCountries.map((country) => (
          <CountryInfo key={country.cca3} data={country} detailed={true} />
        ))}
        </div>*/}
      <div
        style={{
          float: "right",
          width: "50%",
          display: "flex",
          alignItems: "stretch",
          flexDirection: "column",
          justifyContent: "space-between",
        }}
      >
        {usableCountries.map((country) => (
          <CountryInfo key={country.cca3} data={country} detailed={false} />
        ))}
      </div>
    </div>
  );
}

export default Lab1;
